CREATE OR REPLACE FUNCTION public.schc(schemat text)
			RETURNS integer
			AS $$
			BEGIN
				IF NOT EXISTS (SELECT d.oid FROM pg_catalog.pg_namespace as d WHERE d.nspname = schemat) THEN
					EXECUTE 'CREATE SCHEMA ' || schemat;
					RETURN 1;
				ELSE
					RETURN 0;
				END IF;
			END;
			$$ LANGUAGE plpgsql;
