class Archivo_TipologiaDocumental extends BaseArchivo_TipologiaDocumental
{
    public function save(Doctrine_Connection $conn = null)
    {           
        if($this->isNew()) {
            if(sfContext::getInstance()->getUser()->hasAttribute('serie_documental_id')) {
                $this->setSerieDocumentalId(sfContext::getInstance()->getUser()->getAttribute('serie_documental_id'));
            }
        }

        return parent::save($conn);
    }
}
