class Correspondencia_Formato extends BaseCorrespondencia_Formato
{
    public function save(Doctrine_Connection $conn = null)
    {
        if(!$this->get('correspondencia_id'))
        $this->setCorrespondencia_id(sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));

        return parent::save($conn);
    }    

    public function postSave($event)
    {
        $cacheDriver = $this->getTable()->getAttribute(Doctrine_Core::ATTR_RESULT_CACHE);
        $cacheDriver->delete('correspondencia_enviada_list_formato_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
        $cacheDriver->delete('correspondencia_formato_list_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
    }

    public function postDelete($event)
    {
        $cacheDriver = $this->getTable()->getAttribute(Doctrine_Core::ATTR_RESULT_CACHE);
        $cacheDriver->delete('correspondencia_enviada_list_formato_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
        $cacheDriver->delete('correspondencia_formato_list_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
    }
}
