class Correspondencia_FuncionarioCorrelativo extends BaseCorrespondencia_FuncionarioCorrelativo
{
    public function save(Doctrine_Connection $conn = null)
    {
        if($this->isNew())
        {
              if(sfContext::getInstance()->getUser()->getAttribute('pae_funcionario_unidad_id'))
                  $this->setUnidadId(sfContext::getInstance()->getUser()->getAttribute('pae_funcionario_unidad_id'));
        }

        return parent::save($conn);
