class Correspondencia_FuncionarioEmisor extends BaseCorrespondencia_FuncionarioEmisor
{
    public function save(Doctrine_Connection $conn = null)
    {
        if($this->isNew())
        {
            if(!$this->get('correspondencia_id'))
              $this->setCorrespondencia_id(sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
            $this->setFirma('N');
        }

        return parent::save($conn);
    }
    
    public function postSave($event)
    {
        $cacheDriver = $this->getTable()->getAttribute(Doctrine_Core::ATTR_RESULT_CACHE);
        $cacheDriver->delete('correspondencia_enviada_list_funcionario_emisor_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
        $cacheDriver->delete('correspondencia_funcionario_emisor_list_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
    }

    public function postDelete($event)
    {
        $cacheDriver = $this->getTable()->getAttribute(Doctrine_Core::ATTR_RESULT_CACHE);
        $cacheDriver->delete('correspondencia_enviada_list_funcionario_emisor_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
        $cacheDriver->delete('correspondencia_funcionario_emisor_list_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
    }
}
