class Correspondencia_Receptor extends BaseCorrespondencia_Receptor
{
    public function save(Doctrine_Connection $conn = null)
    {
        if(sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'))
            $this->setCorrespondenciaId(sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));

        if(!$this->getEstablecido())
            $this->setEstablecido('S');

        return parent::save($conn);
    }
    
    public function postSave($event)
    {
        $cacheDriver = $this->getTable()->getAttribute(Doctrine_Core::ATTR_RESULT_CACHE);
        $cacheDriver->delete('correspondencia_enviada_list_receptor_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
        $cacheDriver->delete('correspondencia_receptor_list_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
    }

    public function postDelete($event)
    {
        $cacheDriver = $this->getTable()->getAttribute(Doctrine_Core::ATTR_RESULT_CACHE);
        $cacheDriver->delete('correspondencia_enviada_list_receptor_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
        $cacheDriver->delete('correspondencia_receptor_list_'.sfContext::getInstance()->getUser()->getAttribute('correspondencia_id'));
    }
}
