class Correspondencia_UnidadCorrelativo extends BaseCorrespondencia_UnidadCorrelativo
{
    public function save(Doctrine_Connection $conn = null)
    {
        if($this->isNew())
        {
            $this->setStatus('A');
            
            if(!$this->getUnidadId())
                $this->setUnidadId(sfContext::getInstance()->getUser()->getAttribute('funcionario_unidad_id'));
        }
        
        if(!$this->getTipo())
            $this->setTipo('E');

        return parent::save($conn);
    }
}
