class Funcionarios_Funcionario extends BaseFuncionarios_Funcionario
{
    public function save(Doctrine_Connection $conn = null)
    {
        $this->setCi(trim($this->getCi()));
        
        $this->setPrimerNombre(ucwords(strtolower(trim($this->getPrimerNombre()))));
        if($this->get('segundo_nombre'))
            $this->setSegundoNombre(ucwords(strtolower(trim($this->getSegundoNombre()))));
        $this->setPrimerApellido(ucwords(strtolower(trim($this->getPrimerApellido()))));
        if($this->get('segundo_apellido'))
            $this->setSegundoApellido(ucwords(strtolower(trim($this->getSegundoApellido()))));
        
        $this->setEmailInstitucional(strtolower(trim($this->getEmailInstitucional())));
        $this->setEmailPersonal(strtolower(trim($this->getEmailPersonal())));

        return parent::save($conn);
    }
    public function postSave($event)
    {
        $cacheDriver = $this->getTable()->getAttribute(Doctrine_Core::ATTR_RESULT_CACHE);
        $cacheDriver->delete('cache_funcionario_activo');
    }
    
    public function postDelete($event)
    {
        $cacheDriver = $this->getTable()->getAttribute(Doctrine_Core::ATTR_RESULT_CACHE);
        $cacheDriver->delete('cache_funcionario_activo');
    }
}
